  margin-top: 10px;
  margin-bottom: 10px;
  font-size: 3.052rem;
}

/* #region Sidebar */
aside.larrymg_me--sidebar {
  display: flex;
  flex-direction: column;
  padding: 0px 10px;
  background: #e8e8e8;
}

/* Keep last sidebar div in view as visitors scroll through content divs. */
aside.larrymg_me--sidebar div:last-of-type {
  position: sticky;
  top: 0;
}

aside li {
  list-style: none;
  list-style-position: inside;
  padding: 0px 20px 5px 20px;
  background: url("/14a85394bf2c64d10e14c1b6c383f376.svg") no-repeat scroll 0 0 transparent;
  background-size: 6px 6px;
  /* Nudges square-shadow.svg down to the baseline of each li span text. */
  background-position-y: 6px;
}

/* #region Sidebar -- Hyperlinks */
/* #endregion Sidebar -- Hyperlinks */
/* #region Sidebar -- Let's Connect / Social Media Profiles & Icons */
aside.larrymg_me--sidebar div.lets_connect {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-evenly;
  align-content: center;
  height: 50px;
  /* A little bit more margin on the bottom of this div to separate
        it from each of the 'What I'm Learning' h2 elements that follow. */
  margin-bottom: 20px;
  border-bottom: 5px solid #cecece;
  padding: calc(10px + 2.75rem) 0px;
}

aside.larrymg_me--sidebar div.lets_connect a {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 2.75rem;
  height: 2.75rem;
  color: black;
  text-decoration: none;
}

aside.larrymg_me--sidebar div.lets_connect a:link {
  color: black;
}

aside.larrymg_me--sidebar div.lets_connect a:visited {
  color: gray;
}

