/* #region Coding Rules & Assumptions ("CRs&As") -- Coding Rules */
div#container--coding_rules {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  max-width: 100%;
  margin: 3rem 0;
}

div#container--coding_rules div.coding_rule {
  display: grid;
  grid-template-columns: repeat 20, 0.05fr;
  grid-template-columns: repeat(20, 5%);
  /* grid-template-areas: "rule_number rule_text rule_attribution
    explanatory_text explanatory_text in_action";
     */
  grid-template-rows: repeat(3, 0.25fr);
  grid-template-rows: repeat(3, 25%);
  margin: 2vh 0;
  border: 1px dotted black;
  width: 55%;
  background-color: #ffffff;
}

div.coding_rule img {
  max-width: 20px;
}

div.coding_rule span.rule_number {
  display: grid;
  justify-self: start;
  align-self: center;
  place-items: center;
  grid-column: 1/10;
  grid-row: 1;
  min-width: 35px;
  min-height: 35px;
  margin-top: 5px;
  margin-left: 15px;
  background: black;
  color: white;
  border-radius: 50%;
  font-size: 0.9rem;
  /* grid-area: rule_number; */
}

div.coding_rule p.rule_text {
  grid-column: 4/18;
  grid-row: 2;
  /* justify-self: center; */
  max-width: 50ch;
  margin: 35px 0px;
  border-radius: 15px;
  padding: 15px;
  background: rgba(173, 216, 230, 0.5);
  font-size: 1.25rem;
  font-weight: 700;
  /* grid-area: rule_text; */
}

div.coding_rule span.rule_attribution {
  text-align: right;
  grid-column: 11/21;
  grid-row: 1;
  /* grid-area: rule_attribution; */
  width: 100%;
  padding-right: 15px;
  font-size: 0.95rem;
}

div.coding_rule div.rule_justifications {
  grid-column: 1/10;
  grid-row: 3;
}

div.coding_rule div.rule_justifications span {
  display: grid;
  place-items: center;
}

div.coding_rule div.rule_justifications li {
  font-size: 0.75rem;
  max-width: 45ch;
}

div.coding_rule div.rule_concrete_applications {
  display: grid;
  place-items: center;
  grid-column: 11/21;
  grid-row: 3;
}

div.coding_rule div.rule_concrete_applications li {
  font-size: 0.75rem;
  max-width: 45ch;
}

/* #endregion CRs&As -- Coding Rules */
/* #region CRs&As -- Coding Assumptions */
div#container--coding_assumptions {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  max-width: 100%;
  margin: 3rem 0;
}

div#container--coding_assumptions div.coding_assumption {
  display: grid;
  grid-template-columns: repeat(20, 5%);
  /* grid-template-areas: "rule_number rule_text rule_attribution
    explanatory_text explanatory_text in_action";
     */
  grid-template-rows: repeat(3, 25%);
  margin: 2vh 0;
  border: 1px dotted black;
  width: 55%;
  background-color: #ffffff;
}

div.coding_assumption img {
  max-width: 20px;
}

div.coding_assumption span.assumption_number {
  display: grid;
  justify-self: start;
  align-self: center;
  place-items: center;
  grid-column: 1/10;
  grid-row: 1;
  min-width: 35px;
  min-height: 35px;
  margin-top: 5px;
  margin-left: 15px;
  background: black;
  color: white;
  border-radius: 50%;
  font-size: 0.9rem;
  /* grid-area: rule_number; */
}

div.coding_assumption p.assumption_text {
  grid-column: 3/18;
  grid-row: 2;
  /* justify-self: center; */
  max-width: 50ch;
  margin: 35px 0px;
  border-radius: 15px;
  padding: 15px;
  background: rgba(173, 216, 230, 0.5);
  font-size: 1.25rem;
  font-weight: 700;
  /* grid-area: rule_text; */
}

div.coding_assumption span.assumption_attribution {
  text-align: right;
  grid-column: 11/21;
  grid-row: 1;
  /* grid-area: rule_attribution; */
  width: 100%;
  padding-right: 15px;
  font-size: 0.95rem;
}

div.coding_assumption span.no_icons {
  padding-top: 10px;
  margin-left: -10px;
}

div.coding_assumption div.assumption_justifications {
  grid-column: 1/10;
  grid-row: 3;
}

div.coding_assumption div.assumption_justifications span {
  display: grid;
  place-items: center;
}

div.coding_assumption div.assumption_justifications li {
  font-size: 0.75rem;
  max-width: 45ch;
}

div.coding_assumption div.assumption_concrete_applications {
  display: grid;
  place-items: center;
  grid-column: 11/21;
  grid-row: 3;
}

div.coding_assumption div.assumption_concrete_applications li {
  font-size: 0.75rem;
  max-width: 45ch;
}

/* #endregion CRs&As -- Coding Assumptions */
/* #region Media Queries */
@media screen and (min-width: 0px) and (max-width: 575px) {
  main#coding_rules_and_assumptions h2 {
    width: 95%;
    font-size: 2rem;
  }

  div#container--coding_rules div.coding_rule,
div#container--coding_assumptions div.coding_assumption {
    grid-template-rows: repeat(4, 25%);
    width: 100%;
  }

  div.coding_rule div.rule_justifications,
div.coding_assumption div.assumption_justifications {
    grid-column: 1/21;
    grid-row: 3;
  }

  div.coding_rule div.rule_concrete_applications,
div.coding_assumption div.assumption_concrete_applications {
    display: grid;
    place-items: center;
    grid-column: 1/21;
    grid-row: 4;
  }
}
