  padding-left: 10px;
  cursor: move;
  height: 35px;
  line-height: 35px;
  white-space: nowrap;
  overflow: hidden;
  text-overflow: ellipsis;
}

.wb-n,
.wb-s {
  height: 10px;
  position: absolute;
  left: 0;
}

.wb-n {
  top: -5px;
  right: 0;
  cursor: n-resize;
}

.wb-e {
  position: absolute;
  top: 0;
  right: -5px;
  bottom: 0;
  width: 10px;
  cursor: w-resize;
}

.wb-s,
.wb-se,
.wb-sw {
  bottom: -5px;
}

.wb-s {
  right: 0;
  cursor: n-resize;
}

.wb-w {
  position: absolute;
  top: 0;
  left: -5px;
  bottom: 0;
  width: 10px;
  cursor: w-resize;
}

.wb-ne,
.wb-nw,
.wb-sw {
  width: 15px;
  height: 15px;
  position: absolute;
}

.wb-nw {
  top: -5px;
  left: -5px;
  cursor: nw-resize;
}

.wb-ne,
.wb-sw {
  cursor: ne-resize;
}

.wb-ne {
  top: -5px;
  right: -5px;
}

.wb-sw {
  left: -5px;
}

.wb-se {
  position: absolute;
  right: -5px;
  width: 15px;
  height: 15px;
  cursor: nw-resize;
}

.wb-icon {
  float: right;
  height: 35px;
  max-width: 100%;
  text-align: center;
}

.wb-icon * {
  display: inline-block;
  width: 30px;
  height: 100%;
  background-position: center;
  background-repeat: no-repeat;
  cursor: pointer;
  max-width: 100%;
}

.no-close .wb-close,
.no-full .wb-full,
.no-header .wb-header,
.no-max .wb-max,
.no-min .wb-min,
.no-resize .wb-body ~ div,
.winbox.min .wb-body > *,
.winbox.min .wb-full,
.winbox.min .wb-min,
.winbox.modal .wb-full,
.winbox.modal .wb-max,
.winbox.modal .wb-min {
  display: none;
}

.winbox.max .wb-title,
.winbox.min .wb-title {
  cursor: default;
}

.wb-min {
  background-image: url(data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHZpZXdCb3g9IjAgMCAxNiAyIj48cGF0aCBmaWxsPSIjZmZmIiBkPSJNOCAwaDdhMSAxIDAgMCAxIDAgMkgxYTEgMSAwIDAgMSAwLTJoN3oiLz48L3N2Zz4=);
  background-size: 14px auto;
  background-position: center bottom 11px;
}

.wb-max {
  background-image: url(data:image/svg+xml;base64,);
  background-size: 17px auto;
}

.wb-close {
  background-image: url(data:image/svg+xml;base64,);
  background-size: 15px auto;
}

.wb-full {
  background-image: url(data:image/svg+xml;base64,);
  background-size: 16px auto;
}

.winbox.max .wb-body ~ div,
.winbox.min .wb-body ~ div,
.winbox.modal .wb-body ~ div,
.winbox.modal .wb-title,
body.wb-drag iframe {
  pointer-events: none;
}

.max .wb-body {
  margin: 0 !important;
}

.winbox iframe {
  position: absolute;
  width: 100%;
  height: 100%;
  border: 0;
}

.no-animation,
body.wb-drag .winbox {
  transition: none;
}

.winbox.modal:before {
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  background: inherit;
  border-radius: inherit;
}

.winbox.modal:after {
  position: absolute;
  top: -100vh;
  left: -100vw;
  right: -100vw;
  bottom: -100vh;
  background: #0d1117;
