  color: white; */
  /* Dark Grey Lined SVG BG for Job Title "tab". */
  /* background: url("data:image/svg+xml;base64,PHN2ZyB4bWxucz0iaHR0cDovL3d3dy53My5vcmcvMjAwMC9zdmciIHdpZHRoPSI1IiBoZWlnaHQ9IjUiPgo8cmVjdCB3aWR0aD0iNSIgaGVpZ2h0PSI1IiBmaWxsPSIjOWU5ZTllIj48L3JlY3Q+CjxwYXRoIGQ9Ik0wIDVMNSAwWk02IDRMNCA2Wk0tMSAxTDEgLTFaIiBzdHJva2U9IiM4ODgiIHN0cm9rZS13aWR0aD0iMSI+PC9wYXRoPgo8L3N2Zz4="); */
}

section#experience span.employer {
  display: inline-block;
  align-self: flex-end;
  /* max-width: var(--max-width__employer); */
  /* margin-bottom: 25px; */
  margin-top: 2.5px;
  padding-bottom: 15px;
  padding-left: 5px;
  font-family: "resume_main_text", "Century Gothic", Apple Gothic, AppleGothic, URW Gothic L, Avant Garde, Futura, sans-serif;
  font-size: var(--font_size__employer);
  font-weight: var(--font_weight__employer);
  color: var(--light_grey);
}

section#experience span.resume_dates {
  display: inline-block;
  /* Use align-self to ensure jobe title remains flush with solid border line when 
        dates wrap. */
  align-self: flex-end;
  text-align: right;
  font-size: var(--font_size__dates);
  padding-bottom: 2.5px;
  padding-right: 5px;
}

/* Custom styling of unordered lists with job responsibilities. */
section#experience ul.resume_section_list {
  /* background: #f7f6f6; */
  /* background: var(--light_grey); */
  background: #f7f6f6;
  border-radius: 0px 0px 10px 10px;
  border: 1px solid var(--dark_grey);
  padding: 5px;
  margin-bottom: 25px;
  list-style: square;
}

/* #endregion Resume -- Column 2 -- Experience*/
/* #endregion Resume -- Column 2 */
/* #region Resume -- Columns 1 & 2 */
/* #region Resume -- Columns 1 & 2 -- Projects & Side Hustles & Experience */
div.resume_flex_title_and_date_wraps {
  /* Wraps job/project/side hustle titles & date. */
  display: flex;
  align-items: flex-end;
  /* flex-wrap: wrap; */
  /* Pushes dates to far-right of column. */
  justify-content: space-between;
  border-bottom: 2px solid var(--darkest_grey);
}

div.resume_flex_title_and_date_wraps > * {
  max-width: initial;
  width: 50%;
}

/* #endregion Resume -- Columns 1 & 2 -- Projects & Side HUstles & Experience */
/* #endregion */
/* #region Utility Classes */
/* Used this to "unbold" parenthetical comments in "stack" bullet points. */
span.resume_deemphasize {
  font-weight: 500;
}

/* #endregion Utility Classes */
/* #region Media Queries */
/* "Default" settings to hide "wrapped" & show "unwrapped" resume elements prior to @media query changes.

      Implementation logic here is simple: 

      When resume is show as a single column, section-over-section 
      document, you I want Experience (which appears in the right-hand "second" column on larger screens,
      to be the topmost section.  */
div#container--resume-col_2_unwrapped,
section#objective_unwrapped {
  display: initial;
}

div#container--resume-col_2_wrapped,
section#objective_wrapped {
  display: none;
}

@media screen and (min-width: 0px) and (max-width: 1079px) {
  /* Set resume column width custom variable to 100% at breakpoint where right-hand column / column 2 wraps. */
  :root {
    --width__container--resume-col_1: 100%;
    --width__container--resume-col_2: 100%;
  }

  main#my_work--resume {
    /* Add margin to left & right of main element to create "gutters" &
          allow me to use margin: 25px auto to center resume's outermost container. */
    margin-right: 15px;
    margin-left: 15px;
  }

  div.resume--container--header_and_columns {
    /* Pseudo-"freeze" resume's width from 1079px to 835px to 835px &
          Center resume's outermost container.  NOTE: Resume will resize ars at 
          835px width breakpoint.*/
    width: initial;
    max-width: 835px;
    margin: 25px auto;
  }

  div#container--resume-col_2_unwrapped,
section#objective_unwrapped {
    /* Hide "unwrapped" elements on Flex wrap. */
    display: none;
  }

  div#container--resume-col_2_wrapped,
section#objective_wrapped {
    /* Reveal "wrapped" elements on Flex wrap. */
    display: initial;
  }

  div.container--resume-col_2 {
    /* Remove top-margin from container--resume-col_2 class (shared by both 
          wrapped & unwrapped columns) to ensure objective is flush with header. */
    margin-top: 0px;
  }

  div.container--resume-col_2 {
    /* Set min-width & width to 100% and remove left margin to ensure 
        resume sections "stretch" across whole of parent container. */
    min-width: var(--width__container--resume-col_2);
    width: var(--width__container--resume-col_2);
  }

  div.container--resume-col_1 {
    /* set min-width & width to 100% to ensure resume sections "strech" across 
          whole of parent container. */
    min-width: var(--width__container--resume-col_1);
    width: var(--width__container--resume-col_1);
    padding-left: 0px;
  }

  div.resume_section_with_bullets {
    /* Re-declare padding on resume sections with bullets to create "gutters" on either side of 
        text on smaller screens & enhance legibility. */
    padding: 0px 15px;
  }

  section#experience {
    padding-right: 0px;
  }

  section#skills li {
    flex-wrap: wrap;
  }

  section#skills li span:first-of-type {
    width: 50%;
    text-align: left;
  }

  section#skills li span:nth-of-type(2) {
    text-align: right;
    width: 50%;
  }

  section#skills li span:nth-of-type(3) {
    max-width: none;
    width: 100%;
  }

  span.resume_section_list_item_icons {
    display: block;
    flex-grow: 1;
    width: 100%;
  }
}
@media screen and (min-width: 0px) and (max-width: 835px) {
  div.resume--container--header_and_columns {
    /* Pseudo-"freeze" resume's width from 835px to 635px. 
          Resume will shrink to fit screen below 635px. */
    width: initial;
    max-width: 635px;
  }

  div.resume--container--header_and_columns h1 span.clean_break_on_wrap {
    /* Force "Mac Gabhann" to wrap from a screen width of 835px down to 
        ensure enough white space remains between the "Mac Gabhann" and 
        the PDF icon on smaller screens. */
    display: block;
  }

  header#resume_header div.header_contact_info span {
    display: none;
  }

  section#experience span.job_title {
    font-size: calc(var(--font_size__job_titles) * 0.85);
  }

  section#experience span.resume_dates {
    font-size: calc(var(--font_size__dates) * 0.85);
  }

  section#projects_and_side_hustles span.side_hustle {
    /* This code doesn't have to be here - it's actually redundant - but it serves
        to remind me that, below 835px, I align the font sizes for job & side hustle
        titles & dates. */
    font-size: calc(var(--font_size__job_titles) * 0.85);
  }

  section#projects_and_side_hustles span.resume_dates {
    /* This code doesn't have to be here - it's actually redundant - but it serves
        to remind me that, below 835px, I align the font sizes for job & side hustle
        titles & dates. */
    font-size: calc(var(--font_size__dates) * 0.85);
  }
}
@media screen and (min-width: 0px) and (max-width: 725px) {
  header#resume_header h1 {
    font-size: calc(var(--font_size__header_name) * 0.75);
  }
}
@media screen and (min-width: 0px) and (max-width: 545px) {
  section#experience span.resume_dates span.clean_break_on_wrap,
section#projects_and_side_hustles span.resume_dates span.clean_break_on_wrap {
    /* Synchronize styling of dated resume actions on wrap, 
        ensure end date (i.e. start date - end date) string wraps as a single "element" for increased legibility. */
    display: block;
    padding-right: 10px;
  }
}
@media screen and (min-width: 0px) and (max-width: 490px) {
  section.resume_section h2 {
    font-size: calc(var(--font_size__section_titles) * 0.85);
  }
}
@media screen and (min-width: 0px) and (max-width: 525px) {
  section.resume_section h2 {
    /* Turn h2 elements into flex boxes on smaller screen to prevent unsightly wrapping of
        resume section headers. */
    display: flex;
    align-items: center;
  }

  section#courses_and_certifications h2 span:first-of-type,
section#projects_and_side_hustles h2 span:first-of-type,
section#interests_and_hobbies h2 span:first-of-type {
    /* Kind of hacky but this explicitly set height insures that the single-character black
          'sidebar' next to resume section headings streches across the entire red background
          for section headings that wrap. */
    height: 72px;
  }

  section#experience ul ul li,
section#projects_and_side_hustles ul ul li {
    /* Removes "excess" padding from megaphone & stack nested bullets 
        on very small screens for improved legibility. */
    padding: 0px;
  }

  /* Resizes job title by fixed percent ('var(--small_phone_scaledown'). */
  section#experience span.job_title {
    font-size: calc( var(--font_size__job_titles) * var(--small_phone_scaledown) );
  }

  /* Resizes employer by fixed percent ('var(--small_phone_scaledown'). */
  section#experience span.employer {
    font-size: calc(var(--font_size__employer) * var(--small_phone_scaledown));
  }

  /* First of two CSS rules that reduce size of text in each resume section with a font-size of
      'var(--font_size__col_1_titles)' by a fixed percent ('var(--small_phone_scaledown). */
  section#education ul.resume_section_list li.resume_section_list_item span:first-of-type,
section#courses_and_certifications ul.resume_section_list li.resume_section_list_item span:first-of-type,
section#skills ul.resume_section_list li.resume_section_list_item span:first-of-type {
    font-size: calc( var(--font_size__col_1_titles) * var(--small_phone_scaledown) );
  }

  /* Second of two CSS rules that reduce size of text in each resume section with a font-size of
      'var(--font_size__col_1_titles)' by a fixed percent ('var(--small_phone_scaledown). */
  section#projects_and_side_hustles span.side_hustle,
section#interests_and_hobbies div.resume_section_with_bullets > span:first-of-type {
    font-size: calc( var(--font_size__col_1_titles) * var(--small_phone_scaledown) );
  }

  /* Reposition explanatory Irish Studies "hover text" to accomodate reflowed flex box. */
  span#resume_hover_text--irish_studies {
    top: 15px;
    left: -110px;
  }
}
@media screen and (min-width: 0px) and (max-width: 663px) {
  /* Change width of skill family (e.g. "Web Dev") & skill family members
        (e.g. "Vanilla JavaScript") on smaller screens to prevent unsightly wrap
         of text in second span. */
  section#skills li span:first-of-type {
    width: 30%;
    width: 60%;
    text-align: left;
  }

  section#skills li span:nth-of-type(2) {
    text-align: right;
    width: 70%;
    width: 40%;
  }
}
/* #endregion Media Queries */
/* #region larrymg.me in-a-inch index--hello styling.
    TO DO: Componentize this! */
div.larrymg_me_index--hello {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  width: 80%;
}

div.larrymg_me_index--hello h1 {
  margin: 10px 0px;
}

div.larrymg_me_index--hello p:first-of-type {
  font-size: 2.25rem;
  margin-bottom: 10px;
}

/* #endregion index--hello styling */
/* #region Page-specific snippet components. */
main#snippets h2 {
  text-align: center;
  margin-top: 20px;
}

div.snippet_description_container {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-around;
  gap: 2.5rem;
  margin: 20px 0px;
  width: 100%;
  height: auto;
  padding: 20px 0px;
}

div.snippet_description_card {
  background: white;
  min-width: 350px;
  max-width: 45%;
  border: 1px solid var(--light_grey);
}

div.snippet_description_card h3 {
  text-align: center;
  padding-top: 10px;
  font-family: cursive_flourish, cursive;
}

div.snippet_description_card p {
  max-width: 45ch;
  padding: 20px 20px;
}

div.snippet_code_container {
  width: 100%;
  height: auto;
  margin: 25px 0px;
  border: 2px solid var(--darkest_grey);
  padding: 20px 0px;
  background: #efefef;
}

/* #endregion Page-specific snippet components. */
/* #region Sounds of Marathon Christmas Snippet Specific Styling */
:root {
  /* Sounds of Marathon Christmas Custom Variables */
  --santa_black: rgba(1, 2, 3, 0.95);
  --santa_red: #ec5e95;
  --santa_white: #ffff;
  --santa_yellow: #f9fb22;
  --santa_box_shadow_primary: rgba(128, 128, 128, 0.5);
  --santa_body_bg: #1f253c;
  --santa_body_bg-lighter: #353b50;
}

#sounds_of_marathon_christmas .container--keys {
  display: flex;
  justify-content: center;
  align-items: center;
  width: 75%;
  height: auto;
  margin: 0 auto;
}

#sounds_of_marathon_christmas .keys {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  width: 100%;
}

#sounds_of_marathon_christmas .key {
  border: 0.4rem solid var(--santa_white);
  border: 0.4rem solid var(--santa_body_bg-lighter);
  border-radius: 0.5rem;
  margin: 1rem;
  font-family: "Xmas Sweater", sans-serif;
  font-size: 2.25rem;
  padding: 1rem 0.5rem;
  transition: all 0.2s ease;
  width: 10rem;
  text-align: center;
  color: var(--santa_yellow);
  background: var(--santa_body_bg);
  text-shadow: 0 0 0.1rem var(--santa_black);
  box-shadow: 2px 2px 1px var(--santa_box_shadow-primary);
}

#sounds_of_marathon_christmas .key:nth-child(even) {
  transform: rotate(10deg);
}

#sounds_of_marathon_christmas .key:nth-child(odd) {
  transform: rotate(-10deg);
}

#sounds_of_marathon_christmas .playing {
  transform: scale(1.2) !important;
  border-color: var(--santa_yellow);
  box-shadow: 0 0 1rem var(--santa_white);
}

#sounds_of_marathon_christmas kbd {
  display: block;
  margin-bottom: 10px;
  font-size: 2.5rem;
}

#sounds_of_marathon_christmas .sound {
  display: block;
  margin-top: 10px;
  padding-top: 5px;
  background-color: var(--santa_red);
  font-family: "Rubik", sans-serif;
  font-size: 1.25rem;
  color: var(--santa_white);
}

/* #endregion */
/* #region Media Queries */
@media screen and (max-width: 671px) {
  /* Sounds of Marathon Christmas Snippet
  	Unrotate letter blocks on smaller screens. */
  #sounds_of_marathon_christmas .key:nth-child(even),
#sounds_of_marathon_christmas .key:nth-child(odd) {
    transform: rotate(0deg);
  }
}
/* #endregion */
.show_mobile {
  position: relative;
  display: none;
}

p.show_mobile-encourage_larger_screen {
  display: none;
  justify-content: space-between;
  width: 100%;
  border-top: 5px double black;
  padding-top: 15px;
  font-size: 0.85rem;
  font-style: italic;
}

.show_mobile-encourage_larger_screen i.fa-mobile-alt {
  /* position: absolute;
      top: 10px;
      left: -55px; */
  align-self: center;
  border: 1px solid black;
  border-radius: 50%;
  padding: 10px;
  background: white;
  font-size: 2rem;
  font-weight: 800;
}

p.show_mobile-encourage_larger_screen span {
  max-width: 45ch;
}

.show_desktop {
  display: initial;
}

@media screen and (min-width: 0px) and (max-width: 575px) {
  .show_mobile {
    display: initial;
  }

  .show_desktop {
    display: none;
  }

  p.show_mobile-encourage_larger_screen {
    display: flex;
  }

  p.show_mobile-encourage_larger_screen span {
    max-width: 40ch;
  }
}
.text_center {
  text-align: center;
}

.text_bold {
  font-weight: 700;
}

.text_emphasis {
  font-style: italic;
}

.clean_break_on_wrap {
  display: inline-block;
  word-wrap: break-word;
}

.cursive {
  font-family: cursive_flourish, cursive;
}

.code {
  line-height: 1.55;
  border: 1px solid rgba(0, 0, 0, 0.15);
  padding: 0px 15px;
  background: whitesmoke;
  font-family: monospace;
  color: grey;
}

/* #region Import Sass partials into main.scss stylesheet. 
    Note: @import is deprecated!  Use @use & @forward instead.
    See Kevin Powell's video on this: https://www.youtube.com/watch?v=CR-a8upNjJ0 */
/* #endregion Import Sass partials into main.scss stylesheet. */

sources.scss/base/_base.scss:
html {
  scroll-behavior: smooth;
}

body {
  background: #feffd0;
  font-family: "main_text", sans-serif;
  color: black;
}

/* #region Base Hyperlinks */

a,
a:link,
a:visited,
a:focus,
a:hover,
a:active {
  color: blue;
}

/* @endregion */

sources.scss/base/_font-face.scss:
/* #region Self-Hosted Fonts - "main_text" (Kulim Park) */

/*  See https://fonts.google.com/specimen/Kulim+Park */

@font-face {
  font-family: "main_text";
  src: url("../fonts/KulimPark-Regular.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-style: italic;
  src: url("../fonts/KulimPark-Italic.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 200;
  src: url("../fonts/KulimPark-ExtraLight.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 200;
  font-style: italic;
  src: url("../fonts/KulimPark-ExtraLightItalic.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 300;
  src: url("../fonts/KulimPark-Light.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 300;
  font-style: italic;
  src: url("../fonts/KulimPark-LightItalic.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 300;
  font-style: italic;
  src: url("../fonts/KulimPark-LightItalic.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 600;
  src: url("../fonts/KulimPark-SemiBold.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 600;
  font-style: italic;
  src: url("../fonts/KulimPark-SemiBoldItalic.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 700;
  src: url("../fonts/KulimPark-Bold.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "main_text";
  font-weight: 700;
  font-style: italic;
  src: url("../fonts/KulimPark-SemiBoldItalic.ttf") format("truetype");
  font-display: swap;
}

/* #endregion Self-Hosted Fonts - "Main Text" (Kulim Park) */

/* #region Self-Hosted Fonts - "cursive_flourish" (Helvetia Verbundene) */
/* See: https://www.dafont.com/helvetia-verbundene.font */

//  TRYING LEARNING CURVE AS CURSIVE SUBSTITUTE ON TRIAL BASIS: 7/14/21.
