//   font-family: "cursive_flourish";
//   font-style: normal;
//   src: url("../fonts/helvetia-verbundene.ttf") format("truetype");
//   font-display: swap;
// }

@font-face {
  font-family: "cursive_flourish";
  font-style: normal;
  font-weight: 500;
  src: url("../fonts/learning_curve_regular_ot_tt.ttf") format("truetype");
  font-display: swap;
}

@font-face {
  font-family: "cursive_flourish";
  font-style: normal;
  font-weight: 700;
  src: url("../fonts/learning_curve_bold_ot_tt.ttf") format("truetype");
