.show_mobile {
  position: relative;
  display: none;
}

p.show_mobile-encourage_larger_screen {
  display: none;
  justify-content: space-between;
  width: 100%;
  border-top: 5px double black;
  padding-top: 15px;
  font-size: 0.85rem;
  font-style: italic;
}

.show_mobile-encourage_larger_screen i.fa-mobile-alt {
  /* position: absolute;
      top: 10px;
      left: -55px; */
  align-self: center;
  border: 1px solid black;
  border-radius: 50%;
  padding: 10px;
  background: white;
  font-size: 2rem;
  font-weight: 800;
}

p.show_mobile-encourage_larger_screen span {
  max-width: 45ch;
}
.show_desktop {
  display: initial;
}

@media screen and (min-width: 0px) and (max-width: 575px) {
  .show_mobile {
    display: initial;
  }

  .show_desktop {
    display: none;
  }

  p.show_mobile-encourage_larger_screen {
    display: flex;
  }

  p.show_mobile-encourage_larger_screen span {
    max-width: 40ch;
  }
}

.text_center {
  text-align: center;
}

.text_bold {
  font-weight: 700;
}

.text_emphasis {
  font-style: italic;
}

.clean_break_on_wrap {
  display: inline-block;
  word-wrap: break-word;
}

.cursive {
  font-family: cursive_flourish, cursive;
}

.code {
  line-height: 1.55;
  border: 1px solid rgba(0, 0, 0, 0.15);
  padding: 0px 15px;
  background: whitesmoke;
  font-family: monospace;
  color: grey;
}

