sources.scss/pages/_coding_rules.scss:
/*  To Do:  Refactoring  */

/* #region Page-wide Styles */

// main#coding_rules_and_assumptions h2 {
//   position: sticky;
//   top: 0;
//   width: 65%;
//   margin: 0 auto;
//   padding: 15px;
//   border-bottom: 3px dotted black;
//   background: #feffd0;
//   font-size: 3rem;
// }

main#coding_rules_and_assumptions div#coding_rules-header_and_home_link,
main#coding_rules_and_assumptions div#coding_assumptions-header_and_home_link {
  position: sticky;
  top: 0;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 65%;
  margin: 0 auto;
  border-bottom: 3px dotted black;
  background: #feffd0;
}

main#coding_rules_and_assumptions div#coding_rules-header_and_home_link h2,
main#coding_rules_and_assumptions
  div#coding_assumptions-header_and_home_link
  h2 {
  font-size: 3rem;
}

main#coding_rules_and_assumptions div#coding_rules-header_and_home_link i,
main#coding_rules_and_assumptions
  div#coding_assumptions-header_and_home_link
  i {
  padding-top: 10px;
  padding-bottom: 5px;
  color: blue;
  font-size: 3rem;
}
/* a {
  color: rgba(212, 114, 140) !important;
  font-weight: 500;
} */

/* #region Coding Rules & Assumptions ("CRs&As") -- Coding Rules */

article#coding_rules {
}

div#container--coding_rules {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  max-width: 100%;
  margin: 3rem 0;
}

div#container--coding_rules div.coding_rule {
  display: grid;
  grid-template-columns: repeat (20, 0.05fr);
  grid-template-columns: repeat(20, 5%);
  /* grid-template-areas: "rule_number rule_text rule_attribution
    explanatory_text explanatory_text in_action";
     */
  grid-template-rows: repeat(3, 0.25fr);
  grid-template-rows: repeat(3, 25%);
  margin: 2vh 0;
  border: 1px dotted black;
  width: 55%;
  background-color: #ffffff;
}

div.coding_rule img {
  max-width: 20px;
}

div.coding_rule span.rule_number {
  display: grid;
  justify-self: start;
  align-self: center;
  place-items: center;
  grid-column: 1 / 10;
  grid-row: 1;
  min-width: 35px;
  min-height: 35px;
  margin-top: 5px;
  margin-left: 15px;
  background: black;
  color: white;
  border-radius: 50%;
  font-size: 0.9rem;
  /* grid-area: rule_number; */
}

div.coding_rule p.rule_text {
  grid-column: 4 / 18;
  grid-row: 2;
  /* justify-self: center; */
  max-width: 50ch;
  margin: 35px 0px;
  border-radius: 15px;
  padding: 15px;
  background: rgba(173, 216, 230, 0.5);
  font-size: 1.25rem;
  font-weight: 700;
  /* grid-area: rule_text; */
}

div.coding_rule span.rule_attribution {
  text-align: right;
  grid-column: 11/21;
  grid-row: 1;
  /* grid-area: rule_attribution; */
  width: 100%;
  // margin-right: 15px;
  padding-right: 15px;
  font-size: 0.95rem;
}

div.coding_rule div.rule_justifications {
  grid-column: 1/10;
  grid-row: 3;
}

div.coding_rule div.rule_justifications span {
  display: grid;
  place-items: center;
}
div.coding_rule div.rule_justifications li {
  font-size: 0.75rem;
  max-width: 45ch;
}

div.coding_rule div.rule_concrete_applications {
  display: grid;
  place-items: center;
  grid-column: 11/21;
  grid-row: 3;
}

div.coding_rule div.rule_concrete_applications span {
}

div.coding_rule div.rule_concrete_applications li {
  font-size: 0.75rem;
  max-width: 45ch;
}

/* #endregion CRs&As -- Coding Rules */

/* #region CRs&As -- Coding Assumptions */

article#coding_assumptions {
}

div#container--coding_assumptions {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  max-width: 100%;
  margin: 3rem 0;
}

div#container--coding_assumptions div.coding_assumption {
  display: grid;
  grid-template-columns: repeat(20, 5%);
  /* grid-template-areas: "rule_number rule_text rule_attribution
    explanatory_text explanatory_text in_action";
     */
  grid-template-rows: repeat(3, 25%);
  margin: 2vh 0;
  border: 1px dotted black;
  width: 55%;
  background-color: #ffffff;
}

div.coding_assumption img {
  max-width: 20px;
}

div.coding_assumption span.assumption_number {
  display: grid;
  justify-self: start;
  align-self: center;
  place-items: center;
  grid-column: 1 / 10;
  grid-row: 1;
  min-width: 35px;
  min-height: 35px;
  margin-top: 5px;
  margin-left: 15px;
  background: black;
  color: white;
  border-radius: 50%;
  font-size: 0.9rem;
  /* grid-area: rule_number; */
}

div.coding_assumption p.assumption_text {
  grid-column: 3 / 18;
  grid-row: 2;
  /* justify-self: center; */
  max-width: 50ch;
  margin: 35px 0px;
  border-radius: 15px;
  padding: 15px;
  background: rgba(173, 216, 230, 0.5);
  font-size: 1.25rem;
  font-weight: 700;
  /* grid-area: rule_text; */
}

div.coding_assumption span.assumption_attribution {
  text-align: right;
  grid-column: 11/21;
  grid-row: 1;
  /* grid-area: rule_attribution; */
  width: 100%;
  // margin-right: 15px;
  padding-right: 15px;
  font-size: 0.95rem;
}

div.coding_assumption span.no_icons {
  padding-top: 10px;
  margin-left: -10px;
}

div.coding_assumption div.assumption_justifications {
  grid-column: 1/10;
  grid-row: 3;
}

div.coding_assumption div.assumption_justifications span {
  display: grid;
  place-items: center;
}
div.coding_assumption div.assumption_justifications li {
  font-size: 0.75rem;
  max-width: 45ch;
}

div.coding_assumption div.assumption_concrete_applications {
  display: grid;
  place-items: center;
  grid-column: 11/21;
  grid-row: 3;
}

div.coding_assumption div.assumption_concrete_applications span {
}

div.coding_assumption div.assumption_concrete_applications li {
  font-size: 0.75rem;
  max-width: 45ch;
}

/* #endregion CRs&As -- Coding Assumptions */

/* #region Media Queries */

@media screen and (min-width: 0px) and (max-width: 575px) {
  main#coding_rules_and_assumptions h2 {
    width: 95%;
    font-size: 2rem;
  }

  div#container--coding_rules div.coding_rule,
  div#container--coding_assumptions div.coding_assumption {
    grid-template-rows: repeat(4, 25%);
    width: 100%;
  }

  div.coding_rule div.rule_justifications,
  div.coding_assumption div.assumption_justifications {
    grid-column: 1/21;
    grid-row: 3;
  }

  div.coding_rule div.rule_concrete_applications,
  div.coding_assumption div.assumption_concrete_applications {
    display: grid;
    place-items: center;
    grid-column: 1/21;
    grid-row: 4;
  }
}

/* #endregion Media Queries */

sources.scss/pages/_larrymg_me_index.scss:
html {
  scroll-padding: var(--site_wide--scroll_padding);
}

main#larrymg_me_index {
  display: flex;
  flex-direction: row;
  justify-content: center;
  align-items: center;
  height: 100vh;
}

/* #region Sidebar */

/* #region Sidebar -- Let's Connect */

/* Note: The "Let's Connect" part of the sidebar is a COMPONENT shared across several webpages.  
      To view its code, see "sidebar.scss" in scss -- components folder. */

/* #endregion */

/* #region Sidebar -- H2 Elements For All "Weekly" Sections */

/* Note: The <h2> elements that title each "Weekly" section in the main page's sidebar are COMPONENTS shared 
    across several webpages.  To view this code, see "sidebar.scss" in scss -- components folder. */

/* #endregion -- H2 Elements For All "Weekly" Sections */

/* #region What I'm Learning This Week, What I'm Reading This Week, & What I'm Watching This Week Sections */

div.what_im_learning ul,
div.what_im_reading ul,
div.what_im_watching ul {
  font-family: "main_text", sans-serif;
  padding-left: 5px;
}

/* #endregion What I'm Learning This Week, What I'm Reading This Week, & What I'm Watching This Week Sections */

/* #region Sidebar -- This Week's Hot Track & This Week's Hot Laugh */

div.this_weeks_hot_track span.clean_break_on_wrap,
div.this_weeks_hot_laugh span.clean_break_on_wrap {
  /* Add "back" underline to cleanly broken spans for stylistic consistency. */
  text-decoration: underline;
}

/* #endregion Sidebar -- This Week's Hot Track & This Week's Hot Laugh */

/* #region Sidebar -- What I'm Watching This Week */

div.what_im_watching li {
  /* Assumes everything I'm watching would require italicization. */
  font-style: italic;
}

/* #endregion Sidebar -- What I'm Watching This Week */

/* #region Sidebar -- This Week's Hot Track */

div.this_weeks_hot_track {
  display: grid;
}

div.this_weeks_hot_track iframe {
  /* Embedded Spotify player overflows container at max-width: 100%? */
  place-self: center;
  max-width: 100%;
  max-height: 75px;
}

div.this_weeks_hot_track iframe.youtube_iframe {
  max-height: 400px;
}

/* #endregion - Sidebar -- This Week's Hot Track*/

/* #region Sidebar -- This Week's Hot Laugh */

div.this_weeks_hot_laugh iframe,
div.this_weeks_hot_laugh video,
div.this_weeks_hot_laugh img {
  max-width: 100%;
  max-height: 200px;
  border: 1px solid gray;
}

/* #endregion */

/* #endregion - Sidebar */

/* #region Content */

/* Note: div.larrymg_me--content si a COMPONENT shared across several webpages.  
      To view its code, see "content.scss" in scss -- components folder. */

/* #region Content -- Block Quote */

/* Note: Content -- Block Quotes are a COMPONENT shared across several webpages.  
      To view its code, see "content.scss" in scss -- components folder. */

/* #endregion Content -- Block Quote */

/* #region Content -- Hello  */

div.larrymg_me_index--hello {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  width: 80%;
}

div.larrymg_me_index--hello h1 {
  margin: 10px 0px;
}

div.larrymg_me_index--hello img {
  margin: 0 auto;
  width: 30%;
  min-width: 218px;
  height: 30%;
  min-height: 218px;
}

div.larrymg_me_index--hello p:first-of-type {
  font-size: 2.25rem;
  margin-bottom: 10px;
}

div.larrymg_me_index--hello a.test_performance {
  display: inline-block;
  text-align: center;
  align-self: center;
  margin: 25px 0px;
  border: 1px dotted black;
  border-radius: 15px;
  padding: 5px;
  transition: background 0.5s ease-in-out;
}
div.larrymg_me_index--hello a.test_performance p {
  max-width: 25ch;
  font-size: 0.75rem;
}

div.larrymg_me_index--hello a.test_performance:hover {
  background: black;
  color: #fff9fb;
}

/* #endregion Content - - Hello */

/* #region Content -- Intro */

div.larrymg_me_index--intro {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  text-indent: 3%;
  width: 60%;
  max-width: 55ch;
}

div.larrymg_me_index--intro p {
  margin: 10px 0px;
}

div.larrymg_me_index--intro p:nth-of-type(2) {
  text-align: center;
  /* Remove text-indent to prevent weird sizing issues with p element. */
  text-indent: 0;
  margin: 20px 0px;
  font-size: 1.75rem;
}

/* #endregion *?


/* #endregion Content -- Hello & Intro */

/* #endregion Content */

/* #region Content -- Explore */

div.larrymg_me--content nav.explore_concise {
  width: 80%;
  max-width: 1102px;
  margin: 50px 0px;
  border: 1px solid black;
  border-radius: 10px;
  padding: 15px;
  background: white;
}

div.larrymg_me--content
  nav.explore_concise
  div.concise_menu_instructions-nav_gray_block_and_text {
  display: flex;
  flex-direction: column;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  border-bottom: 5px solid #cecece;
}

div.concise_menu_instructions-nav_gray_block_and_text
  h2.concise_menu_instructions-nav_gray_block {
  text-align: center;
  border-radius: 10px;
  padding: 15px;
  background: rgba(206, 206, 206, 0.45);
  font-size: 1.25rem;
  width: 80%;
}

div.concise_menu_instructions-nav_gray_block_and_text
  div.concise_menu_instructions-text {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
}

div.concise_menu_instructions-nav_gray_block_and_text
  div.concise_menu_instructions-text
  p {
  max-width: 55ch;
  margin-top: 10px;
  margin-bottom: 10px;
}

div.concise_menu_instructions-nav_gray_block_and_text
  div.concise_menu_instructions-text
  p:first-of-type {
  text-align: center;
  margin-top: 20px;
}

div.concise_menu_instructions-nav_gray_block_and_text
  div.concise_menu_instructions-text
  p:last-of-type {
  margin-bottom: 20px;
}

div.larrymg_me--content nav.explore_concise div.explore_link_roll {
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  justify-content: space-evenly;
  max-width: 100%;
}

div.larrymg_me--content nav.explore_concise div.explore_link_roll a {
  padding: 15px;
}

div.larrymg_me--content nav.explore_verbose {
  display: grid;
  grid-gap: 1rem;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  /* width: 90%;
  max-width: 1102px; */
  width: clamp(90%, 90%, 1102px);
  margin: 50px 0px;
  /* To prevent scrolling to right. */
  /* overflow: hidden; */
}

div.explore_item {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-height: 225px;
  border: 1px solid black;
  padding: 8px;
  background: #cecece;
  transition: all 0.5s ease-in-out;
}

div.explore_item:hover {
  background: #fff9fb;
  transform: scale(1.045);
}

div.explore_item h3 {
  /* Use flex-grow to produce a semi-centered alignment of card text
      across cards. */
  flex-grow: 1;
  text-align: center;
  /* Bottom margin ensures a minimal amount of space between h2 &
      proceeding p element on cards with more text. */
  margin-bottom: 3%;
  color: #252627;
}

div.explore_item p {
  /* Use flex-grow to ensure chenvron icons are flush with bottom of card, 
  regardless of how much text is in indivudal cards. */
  flex-grow: 1;
  /* Prevents overly long lines on smaller screens when cards explore_item divs
      stack one on top of the other. */
  max-width: 40ch;
  padding: 0px 10px;
  color: rgba(128, 128, 128, 0.6);
  transition: color 0.5s ease-in-out;
}

div.explore_item:hover p {
  color: black;
}

div.explore_item p + a {
  /* Styles chevrons at bottom of cards that link back to nav menu. */
  text-decoration: none;
  /* Use adjacent sibling selector to prevent resizing of linked h2 elements. */
  font-size: 1.25rem;
  transition: transform 0.25s linear;
}

div.explore_item a:hover,
div.explore_item a:focus {
  /* Move chevrons at bottom of cards up slightly & enalrge on hover. */
  transform: translateY(-5px) scale(1.2);
}

div#my_work {
  position: relative;
}

p#my_work-current_job_status {
  position: absolute;
  bottom: 10px;
  right: 8px;
  display: inline-block;
  border-radius: 15px;
  padding: 4px;
  background: white;
  font-size: 0.85rem;
  font-weight: 700;
  color: black;
  transition: all 0.25s ease-in-out;
}

div.explore_item:hover p#my_work-current_job_status {
  background: blue;
  color: white;
}

/* #endregion Content -- Explore */

/* #endregion Content */

/* #region Media Queries */
@media screen and (min-width: 0px) and (max-width: 575px) {
  div.larrymg_me--grid_container--sidebar_and_content {
    display: grid;
    grid-template-columns: 1fr;
    width: 100%;
    height: 100%;
    place-items: center;
  }

  div.larrymg_me--content {
    padding: 0px 5px;
  }

  div.larrymg_me--content div.block_quotes {
    max-width: 90%;
    margin-top: 10px;
  }

  div.larrymg_me_index--hello {
    max-width: 90%;
  }

  div.larrymg_me_index--hello p:first-of-type {
    font-size: 2.25rem;
  }

  div.larrymg_me_index--hello img {
    margin: 0 auto;
    width: 80%;
    height: 80%;
  }

  div.larrymg_me_index--intro p:nth-of-type(2) {
    font-size: 1.5rem;
  }

  p#my_work-current_job_status {
    display: none;
  }
}

@media screen and (min-width: 0px) and (max-width: 1069px) {
  /* Tweak layout of Conciuse Menu Instructions on flex-wrap. */
  p#explore_concise_menu-instructions {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    border-bottom: 5px solid #cecece;
    padding-bottom: 15px;
  }

  p#explore_concise_menu-instructions span:first-of-type {
    margin-bottom: 20px;
  }
}

@media screen and (min-width: 0px) and (max-width: 1075px) {
  div.this_weeks_hot_track iframe {
    /* Embedded Spotify player overflows container at max-width: 100%? */
    max-width: 75%;
  }
}

/* #endregion Media Queries */

sources.scss/pages/_my_web_dev_journey.scss:
main#my_web_dev_journey {
}

/* #region Sidebar */

main#my_web_dev_journey aside ul {
  margin: 0;
  padding: 0;
}

div#tldr {
  margin-top: 100px;
  color: rgba(128, 128, 128, 0.6);
  transition: color 0.5s ease-in-out;
}

div#tldr h2 {
  color: black !important;
}

div#tldr:hover {
  color: black;
}

div#tldr p#hound_of_heaven_quote {
  font-family: cursive_flourish, cursive;
  margin-bottom: 20px;
}

div#tldr a {
  font-size: 1rem;
}

div#tldr p {
  margin: 10px 0px;
  font-size: 0.85rem;
}

div#tldr details,
div#tldr details a {
  font-weight: 700;
  font-size: 0.85rem;
}

div#tldr details p {
  display: none;
  height: 0px;
  border-bottom: 2px solid #cecece;
}

div#tldr details[open] p {
  display: inline-block;
  height: auto;
  padding: 5px 15px 15px 15px;
}

div#tldr details[open] {
  border-bottom: 5px solid black;
}

div#tldr details:first-of-type {
  margin-top: 25px;
  margin-bottom: 30px;
}

div#tldr details {
  padding: 2px;
  background: white;
}

div#tldr summary {
  text-align: left;
  border: 1px solid black;
  padding: 5px;
  font-size: 0.95rem;
  background: #cecece;
}

/* #endregion */

/* #region Content */

main#my_web_dev_journey div#container--home_and_header {
  position: sticky;
  top: 0;
  justify-content: space-evenly;
  align-items: center;
  width: 100%;
  border-bottom: 3px dotted black;
  background: #feffd0;
  z-index: 2;
}

main#my_web_dev_journey div#container--home_and_header i {
  margin-top: 10px;
  margin-bottom: 10px;
  font-size: 3.052rem;
}

main#my_web_dev_journey div.larrymg_me--content iframe {
  width: 100%;
  height: 250px;
  border-radius: 10px;
}

main#my_web_dev_journey div.container--iframe_and_caption {
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  width: 100%;
  margin-top: 25px;
  margin-bottom: 25px;
}

main#my_web_dev_journey span#iframe_journey_vaporwave_caption {
  margin-top: 10px;
  font-family: cursive_flourish, cursive;
  font-size: 1.25rem;
  font-weight: 600;
  color: #b9b9b9;
}

main#my_web_dev_journey div.container--journey_text {
  display: grid;
  place-self: center;
  grid-template-columns: 1fr 55ch 1.25fr;
  grid-template-columns: 55ch 1fr;
  grid-template-rows: repeat(auto, 1fr);
  gap: 2.5rem;
  width: 90%;
}

main#my_web_dev_journey div.container--journey_text ul {
  /* Styles ul to the right of .journey_text. */
  display: flex;
  flex-direction: column;
  align-self: flex-start;
  /* This value creates a "gap" between text & links by pushing links all the way to the right 
        of their respective columns. */
  justify-self: left;

  /* This value controls the size of the minimum "gap" between text & links. */
  min-width: 75%;
}

main#my_web_dev_journey div.container--journey_text li {
  /* Styles li to the right of .journey_text. */
  display: flex;
  list-style: none;
  list-style-position: inside;
  /* This value controls the maximum size of the link text's lines. */
  max-width: 35ch;
  // Add extra padding to bottom of links.
  padding: 0px 20px 10px 20px;
  // background: url("../images/my_work/resume/bullets/square-shadow.svg")
  //   no-repeat scroll 0 0 transparent;
  // background-size: 6px 6px;
  /* Nudges square-shadow.svg down to the baseline of each li span text. */
  // background-position-y: 6px;
  font-size: 0.85rem;
}

main#my_web_dev_journey div.container--journey_text li i {
  /* Styles Font Awesome icons to the right of .journey_text. */
  align-self: center;
  font-size: 1.1rem;
  color: darkgrey;
  transform: translateX(-20px);
}

main#my_web_dev_journey div.container--journey_text ul#moonrocks {
  // Align @TheMooninites tweet with "Nerd Alert" picture.
  align-self: flex-end;
}

main#my_web_dev_journey div.container--journey_text p {
  max-width: 55ch;
  margin-top: 10px;
  margin-bottom: 10px;
  margin-left: 25px;
}

main#my_web_dev_journey div.container--journey_text p.paragraph_emphasis {
  max-width: 45ch;
  font-size: 1.25rem;
  font-weight: 700;
}

main#my_web_dev_journey div.container--journey_text p.grid_text_center {
  place-self: center;
}

// div.container--journey_text--1 {
//   grid-column: 1 / 3;
//   background: green;
// }

main#my_web_dev_journey div.container--journey_text .journey_text {
  grid-column: 1 / 2;
}

main#my_web_dev_journey div.container--journey_text div.journey_text,
main#my_web_dev_journey div.container--journey_text div.journey_text ul,
main#my_web_dev_journey div.container--journey_text div.journey_text li {
  display: flex;
  flex-direction: column;
}

main#my_web_dev_journey div.container--journey_text div.journey_text li {
  list-style: none;
  list-style-position: inside;
  background: url("../images/my_work/resume/bullets/square-shadow.svg")
    no-repeat scroll 0 0 transparent;
  background-size: 6px 6px;
  /* Nudges square-shadow.svg down to the baseline of each li span text. */
  background-position-y: 6px;
  /* This value controls the maximum size of the link text's lines. */
  max-width: 55ch;
  // Add extra padding to bottom of links.
  padding: 0px 20px 10px 20px;
  // background: url("../images/my_work/resume/bullets/square-shadow.svg")
  //   no-repeat scroll 0 0 transparent;
  // background-size: 6px 6px;
  /* Nudges square-shadow.svg down to the baseline of each li span text. */
  // background-position-y: 6px;
  font-size: 0.95rem;
  font-weight: 700;
}

main#my_web_dev_journey figure.journey_figure {
  grid-column: 1 / 2;
  align-self: center;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  min-width: 223px;
  max-width: 350px;
  margin-top: 30px;
  border: 2px solid darkgrey;
  padding: 15px;
  background: #e8e8e8;
  opacity: 0.9;
  box-shadow: 2px 1px 1px 1px black;
}

main#my_web_dev_journey figure.journey_figure img {
  width: 100%;
  height: 100%;
  box-shadow: 1px 1px 1px 1px black;
  border-radius: 10px;
  background: white;
}

main#my_web_dev_journey figure.journey_figure figcaption {
  display: grid;
  place-items: center;
  // font-style: italic;
  width: 100%;
  margin-top: 15px;
  padding: 10px;
  // Currently loaded into CSS via Google link.
  // Need to go back and host this font locally if I decided to use it!
  font-family: "Kalam", cursive;
  font-size: 0.95rem;
  font-size: 15px;
  font-weight: 500;
  background: white;
  border: 1px solid darkgrey;
  // transform: rotate(-5deg);
}

main#my_web_dev_journey figure.click_here_smaller_text span:first-of-type {
  margin-bottom: 15px;
}

main#my_web_dev_journey figure.click_here_smaller_text span:nth-of-type(2) {
  font-size: 0.85rem;
}

main#my_web_dev_journey div.journey_text#final_paragraph_with_bullets li {
  display: initial;
  font-weight: 500;
}

main#my_web_dev_journey
  div.journey_text#final_paragraph_with_bullets
  li
  span.first_line {
  font-size: 1.1rem;
  font-weight: 700;
}

/* #endregion */

/* #region Media Queries */
@media screen and (min-width: 0px) and (max-width: 575px) {
  main#my_web_dev_journey div.larrymg_me--grid_container--sidebar_and_content {
    display: grid;
    grid-template-columns: 1fr;
    width: 100%;
    height: 100%;
    place-items: center;
  }

  main#my_web_dev_journey div.block_quotes {
    max-width: 90%;
    margin-top: 10px;
  }
}

@media screen and (min-width: 0px) and (max-width: 955px) {
  main#my_web_dev_journey div#container--home_and_header {
    position: sticky;
    top: 0;
    justify-content: space-evenly;
    align-items: center;
    width: 100%;
    border-bottom: 3px dotted black;
    background: #feffd0;
    z-index: 2;
  }

  main#my_web_dev_journey div.larrymg_me--content iframe {
    width: 75%;
  }

  main#my_web_dev_journey div.container--iframe_and_caption {
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    width: 100%;
    margin-top: 25px;
    margin-bottom: 25px;
  }

  main#my_web_dev_journey div.container--journey_text {
    display: grid;
    place-self: center;
    grid-template-columns: 1fr;
    grid-template-rows: repeat(auto, 1fr);
    gap: 0.5rem;
    width: 100%;
  }
}

@media screen and (min-width: 0px) and (max-width: 1075px) {
  main#my_web_dev_journey div.this_weeks_hot_track iframe {
    /* Embedded Spotify player overflows container at max-width: 100%? */
    max-width: 75%;
  }
}

@media screen and (min-width: 0px) and (max-width: 1120px) {
  main#my_web_dev_journey div.container--journey_text ul {
    display: none;
  }
  main#my_web_dev_journey div.container--journey_text {
    grid-template-columns: auto;
  }

  main#my_web_dev_journey div.container--journey_text > * {
    place-self: center;
  }
}

@media screen and (min-width: 1440px) {
  main#my_web_dev_journey div.container--journey_text {
    /* Push <ul> paired with .journey_text further right on larger screens.
          Note that we cannot use "gap" here because it will add an equal amount of gap 
          between each successive block of journey_text.  Not a good look! */
    column-gap: 15.5rem;
  }
}

/* #endregion Media Queries */

sources.scss/utilities/_utility_classes.scss:
.show_mobile {
  position: relative;
  display: none;
}

p.show_mobile-encourage_larger_screen {
  display: none;
  justify-content: space-between;
  width: 100%;
  border-top: 5px double black;
  padding-top: 15px;
  font-size: 0.85rem;
  font-style: italic;
}

.show_mobile-encourage_larger_screen i.fa-mobile-alt {
  /* position: absolute;
      top: 10px;
      left: -55px; */
  align-self: center;
  border: 1px solid black;
  border-radius: 50%;
  padding: 10px;
  background: white;
  font-size: 2rem;
  font-weight: 800;
}

p.show_mobile-encourage_larger_screen span {
  max-width: 45ch;
}
.show_desktop {
  display: initial;
}

@media screen and (min-width: 0px) and (max-width: 575px) {
  .show_mobile {
    display: initial;
  }

  .show_desktop {
    display: none;
  }

  p.show_mobile-encourage_larger_screen {
    display: flex;
  }

  p.show_mobile-encourage_larger_screen span {
    max-width: 40ch;
  }
}

.text_center {
  text-align: center;
}

.text_bold {
  font-weight: 700;
}

.text_emphasis {
  font-style: italic;
}

.clean_break_on_wrap {
  display: inline-block;
  word-wrap: break-word;
}

.cursive {
  font-family: cursive_flourish, cursive;
}

.code {
  line-height: 1.55;
  border: 1px solid rgba(0, 0, 0, 0.15);
  padding: 0px 15px;
  background: whitesmoke;
  font-family: monospace;
  color: grey;
}
